CREATE TABLE public.categories (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), user_id UUID NOT NULL REFERENCES public.users(id) ON DELETE CASCADE, name TEXT NOT NULL, color TEXT DEFAULT '#3B82F6', created_at TIMESTAMPTZ DEFAULT NOW(), updated_at TIMESTAMPTZ DEFAULT NOW(), UNIQUE(user_id, name)); ALTER TABLE public.categories ENABLE ROW LEVEL SECURITY; CREATE POLICY "Users can view own categories" ON public.categories FOR SELECT USING (auth.uid() = user_id); CREATE POLICY "Users can insert own categories" ON public.categories FOR INSERT WITH CHECK (auth.uid() = user_id); CREATE POLICY "Users can update own categories" ON public.categories FOR UPDATE USING (auth.uid() = user_id); CREATE POLICY "Users can delete own categories" ON public.categories FOR DELETE USING (auth.uid() = user_id); CREATE INDEX idx_categories_user_id ON public.categories(user_id); CREATE INDEX idx_categories_name ON public.categories(name);