CREATE OR REPLACE FUNCTION update_updated_at_column() RETURNS TRIGGER AS $$ BEGIN NEW.updated_at = NOW(); RETURN NEW; END; $$ LANGUAGE plpgsql; CREATE TRIGGER update_users_updated_at BEFORE UPDATE ON public.users FOR EACH ROW EXECUTE FUNCTION update_updated_at_column(); CREATE TRIGGER update_lists_updated_at BEFORE UPDATE ON public.lists FOR EACH ROW EXECUTE FUNCTION update_updated_at_column(); CREATE TRIGGER update_tasks_updated_at BEFORE UPDATE ON public.tasks FOR EACH ROW EXECUTE FUNCTION update_updated_at_column(); CREATE OR REPLACE FUNCTION update_task_completed_at() RETURNS TRIGGER AS $$ BEGIN IF NEW.status = 'complete' AND OLD.status = 'incomplete' THEN NEW.completed_at = NOW(); ELSIF NEW.status = 'incomplete' AND OLD.status = 'complete' THEN NEW.completed_at = NULL; END IF; RETURN NEW; END; $$ LANGUAGE plpgsql; CREATE TRIGGER update_task_completed_at_trigger BEFORE UPDATE ON public.tasks FOR EACH ROW EXECUTE FUNCTION update_task_completed_at();